CREATE TABLE IF NOT EXISTS Realisateur(id INT primary key auto_increment, nom VARCHAR(100), prenom VARCHAR(100), date_naissance TIMESTAMP, celebre BOOLEAN);

CREATE TABLE IF NOT EXISTS Film(id INT primary key auto_increment, titre VARCHAR(100), duree INT, realisateur_id INT, date_sortie DATE, poster_url VARCHAR(500), date_ajout TIMESTAMP, watch_providers VARCHAR(2000), rating DECIMAL(2,1), synopsis TEXT);

